import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Electrician.css';

const Electrician = () => {
  const [provider, setProvider] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProvider = async () => {
      const urlParams = new URLSearchParams(window.location.search);
      const email = urlParams.get('email');
      if (!email) {
        setError('No email provided');
        return;
      }

      try {
        const response = await axios.get(`http://localhost:5000/service-provider/${email}`);
        setProvider(response.data);
      } catch (error) {
        setError('Error fetching service provider');
      }
    };

    fetchProvider();
  }, []);

  return (
    <div className="electrician-container">
      {error && <p className="error-message">{error}</p>}
      {provider && (
        <div className="provider-card">
          <h3>{provider.service}</h3>
          <p>Experience: {provider.experience} years</p>
          <p>Address: {provider.address}</p>
          <p>Phone: {provider.phone}</p>
        </div>
      )}
    </div>
  );
};

export default Electrician;
