import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Electrician.css';

const Electrician = ({ id }) => {
  const [provider, setProvider] = useState(null);

  useEffect(() => {
    const fetchProvider = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/service-provider/${id}`);
        setProvider(response.data);
      } catch (error) {
        console.error('Error fetching service provider:', error);
      }
    };

    fetchProvider();
  }, [id]);

  if (!provider) {
    return <p>Loading...</p>;
  }

  return (
    <div className="provider-card">
      <img
        className="provider-image"
        src={`data:image/jpeg;base64,${provider.image}`}
        alt="Service Provider"
      />
      <div className="provider-info">
        <h2>{provider.service} Provider</h2>
        <p><strong>Experience:</strong> {provider.experience} years</p>
        <p><strong>Address:</strong> {provider.address}</p>
      </div>
    </div>
  );
};

export default Electrician;
