import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Electrician.css';

const Electrician = ({ email }) => {
  const [provider, setProvider] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProvider = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/service-provider/${email}`);
        setProvider(response.data);
      } catch (err) {
        setError('Error fetching service provider');
        console.error('Error fetching service provider:', err);
      }
    };

    fetchProvider();
  }, [email]);

  if (error) return <div>{error}</div>;
  if (!provider) return <div>Loading...</div>;

  // Convert image buffer to base64
  const imageSrc = provider.image ? `data:image/jpeg;base64,${Buffer.from(provider.image).toString('base64')}` : '';

  return (
    <div className="provider-card">
      <img src={imageSrc} alt="Provider" className="provider-image" />
      <h3>{provider.service}</h3>
      <p><strong>Experience:</strong> {provider.experience} years</p>
      <p><strong>Address:</strong> {provider.address}</p>
      <p><strong>Email:</strong> {provider.email}</p>
    </div>
  );
};

export default Electrician;
