import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Carpenter.css';

const Carpenter = () => {
  const [carpenters, setCarpenters] = useState([]);

  useEffect(() => {
    const fetchCarpenters = async () => {
      try {
        const response = await axios.get('http://localhost:5000/service-providers/carpenter');
        setCarpenters(response.data);
      } catch (error) {
        console.error('Error fetching carpenters:', error);
      }
    };

    fetchCarpenters();
  }, []);

  return (
    <div className="service-provider-container">
      <h2>Carpenters</h2>
      <div className="provider-cards">
        {carpenters.map((carpenter) => (
          <div className="provider-card" key={carpenter.email}>
            <h3>{carpenter.service}</h3>
            <p>Experience: {carpenter.experience} years</p>
            <p>Address: {carpenter.address}</p>
            <p>Email: {carpenter.email}</p>
            {carpenter.image && <img src={`data:image/jpeg;charset:utfbase64,${carpenter.image}`} alt="Service Provider" />}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Carpenter;
