import React, { useState } from 'react';
import axios from 'axios';
import './Become.css'
const Become = () => {
  const [formData, setFormData] = useState({
    service: '',
    experience: '',
    address: '',
    email: '',
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleFileChange = (e) => {
    setFormData((prevData) => ({
      ...prevData,
      image: e.target.files[0],
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { service, experience, address, email, image } = formData;

    if (!service || !experience || !address || !email || !image) {
      alert('All fields are required');
      return;
    }

    const formDataToSend = new FormData();
    formDataToSend.append('service', service);
    formDataToSend.append('experience', experience);
    formDataToSend.append('address', address);
    formDataToSend.append('email', email);
    formDataToSend.append('image', image);

    try {
      const response = await axios.post('http://localhost:5000/become-service-provider', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Error submitting form');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Service</label>
        <select name="service" value={formData.service} onChange={handleChange}>
          <option value="">Select a service</option>
          <option value="electrician">Electrician</option>
          <option value="carpenter">Carpenter</option>
          <option value="plumber">Plumber</option>
          <option value="janitor">Janitor</option>
          <option value="mason">Mason</option>
          <option value="gardener">Gardener</option>
          <option value="mechanic">Mechanic</option>
          <option value="painter">Painter</option>
        </select>
      </div>
      <div>
        <label>Experience (years)</label>
        <input type="number" name="experience" value={formData.experience} onChange={handleChange} />
      </div>
      <div>
        <label>Address</label>
        <input type="text" name="address" value={formData.address} onChange={handleChange} />
      </div>
      <div>
        <label>Email</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} />
      </div>
      <div>
        <label>Image</label>
        <input type="file" name="image" onChange={handleFileChange} />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Become;
